var UIcontrol = (function(){

    var algoName = ["none", "Dijkstras", "BFS", "DFS", "A*", "Swarm"];
    var speedVal = [NaN, 70, 30, 10];

    let si=10,sj=10,ei=10,ej=30;
    var mouseDown = false;

    var pressedNodeStatus = "normal";
    

    for (let i = 0; i < 20; i++) {
        for (let j = 0; j < 51; j++) {
            let a = i+10,b = j+10;
            let currentId = a+""+b;
            let currentElement = document.getElementById(currentId);
            currentElement.addEventListener("onmousedown",function(){
                // e.preventDefault();
                    mouseDown = true;
                    if ((i === si && j === sj) || (i === ei && j === ej)) {
                        pressedNodeStatus = (i === si) ? "start" : "target";
                    } else {
                        pressedNodeStatus = "normal";
                        // board.changeNormalNode(currentNode);
                    }
            });
            currentElement.onmouseup = () => {
                    mouseDown = false;
                    if (pressedNodeStatus === "target") {
                        ei = i;
                        ej = j;
                    } else if (pressedNodeStatus === "start") {
                        si = i;
                        sj = j;
                    } 
                    pressedNodeStatus = "normal";
            }
            currentElement.onmouseenter = () => {
                    if (mouseDown && pressedNodeStatus !== "normal") {
                        currentElement.classList.add("cur")
                        if (pressedNodeStatus === "target") {
                            ei = i;
                            ej = j;
                        } else if (pressedNodeStatus === "start") {
                            si = i;
                            sj = j;
                        }
                    } else if (mouseDown) {
                        currentElement.classList = "";
                    }
            }
            currentElement.onmouseleave = () => {
                    if (mouseDown && pressedNodeStatus !== "normal") {
                        currentElement.classList.add("cur");
                    }
            }
        }
    }


    return {
        input: function () {
            return {
                algorithm: algoName[document.getElementById("algo").value],
                speed: speedVal[document.getElementById("speed").value],
                src_i: si,
                src_j: sj,
                dst_i: ei,
                dst_j: ej
            }
        },
        clear:function(){
            for(let i=0;i<20;i++){
                for(let j=0;j<51;j++){
                    let a = i+10;
                    let b = j+10;
                    document.getElementById(a + "" + b).className = "";
                }
            }
        }
    }
    
})();

export default UIcontrol;
