var dx = [-1, 0, 0, 1];
var dy = [0, -1, 1, 0];

var Board = new Array(20);
for (let i = 0; i < 20; i++) {
    Board[i] = new Array(51);
    for (let j = 0; j < 51; j++) {
        Board[i][j] = false;
    }
}
var Path = new Array(20);
for (let i = 0; i < 20; i++) {
    Path[i] = new Array(51);
}
var timer = 100;
function fill(i, j, pre_i, pre_j) {
    setTimeout(function () {
        let x = pre_i + 10;
        let y = pre_j + 10;
        // document.getElementById(x + "" + y).classList.remove("cur");
        if (pre_i !== 10 || pre_j !== 10)
            document.getElementById(x + "" + y).classList.add("abc");
        x = i + 10; y = j + 10;
        // document.getElementById(x + "" + y).classList.add("cur");
    }, timer);
}

class node{
    constructor(i,j){
        this.x = i;
        this.y = j;
    }
}
function bfs(sx,sy,ex,ey){

    var pending_nodes = [];
    let t = new node(sx,sy);
    pending_nodes.push(t);

    while(pending_nodes.length !== 0){
        timer += 10;
        var tmp = pending_nodes.shift();
        // console.log(tmp);

        if (tmp.x === ex && tmp.y === ey)
            return true;

        for(let k = 0;k<4;k++){
            let nx = tmp.x+dx[k];
            let ny = tmp.y+dy[k];
            if(nx >= 0 && nx <=19 && ny >= 0 && ny<=50){
                fill(nx, ny, tmp.x, tmp.y);
                if(Board[nx][ny] === false){
                    Path[nx][ny] = new node(tmp.x,tmp.y);
                    if (nx === ex && ny === ey) return true;
                    Board[nx][ny] = true;
                    pending_nodes.push(new node(nx,ny));
                }
            }
        }
    }
}


document.getElementById("2050").classList.add("end");
document.getElementById("2020").classList.add("start");

