var dx = [-1, -1, -1, 0, 0, 1, 1, 1];
var dy = [-1, 0, 1, -1, 1, -1, 0, 1];

var Board = new Array(20);
for(let i =0;i<20;i++){
    Board[i] = new Array(51);
    for(let j=0;j<20;j++){
        Board[i][j] = false;
    }
}
var timer = 10;
function fill(i, j) {
    setTimeout(function () {
        let x = i + 10;
        let y = j + 10;
        document.getElementById(x + "" + y).classList.add("abc");
    }, 50 *timer);
}
function dfs(sx,sy,ex,ey){

    Board[sx][sy] = true;
    fill(sx,sy);
    timer += 10;

    if(sx === ex && sy === ey){
        return true;
    }

    console.log(sx + " " + sy);

    for(let k=0;k<8;k++){
        let nx = sx + dx[k];
        let ny = sy + dy[k];

        if(nx <= ex && nx >= 0 && ny <= ey && ny >= 0){
            if(Board[nx][ny] === false){
                let t = dfs(nx, ny, ex, ey);
                if (t === true) {
                    return true;
                }
            }
        }
    }
    return false;

}

let path = dfs(0,5,17,11);
